import React, { Component } from 'react';
import './App.css';
import IconChange from './IconChange'


/*****************************************/ 
/*               API DATA                */
/*****************************************/

// const API_KEY = '336eac8083532c51fc2ac7331f8f597f'
// const URL = 'http://api.weatherstack.com/'

/*****************************************/ 
/*            Child Components           */
/*****************************************/

// // onChange={this.onSearchChange()} pass as a property in the input

// // props={this.onSearchChange()} data={this.filteredData} pass as porperty in Card

/*****************************************/ 
/*            App Component              */
/*****************************************/

export class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
        api: [],  
        searchfield: 'Helsinki',
        city: '',
        country: '',
        localtime: '',
        temperature: '',
        weather_descriptions: ''
    }     
  }

  componentDidMount() {
    this.getAPI()
  }
      //`${URL}/current?access_key=${API_KEY}&query=${this.state.searchfield}`
    getAPI = async () => {
      const apiCall = await fetch(`http://api.weatherstack.com/current?access_key=336eac8083532c51fc2ac7331f8f597f&query=Helsinki`);
      const data = await apiCall.json();
       this.setState({
          city: data.location.name,
          country: data.location.country,
          localtime: data.location.localtime,
          temperature: data.current.temperature,
          weather_descriptions: data.current.weather_descriptions 

      })
    }

  onSearchChange = (event) => {
            this.setState({searchfield: event.target.value })            
  }




  render() {

    const {city, country, temperature, weather_descriptions, image, localtime } = this.state;

    // const filteredData = this.state.data.filter((data) => (
    // data.name.toLowerCase().includes(this.state.searchfield.toLowerCase())))


        return (
            <div className="app">
             <header className="header">
                <h1 className="header__h1">How's the weather today?</h1>
                <input className="" name="searchBox" type="search" placeholder="search..." onChange={this.onSearchChange} />
             </header>

             <main className="main">
              <section>
                  <div className="card">
                    <p>{this.state.city}</p> 
                    <p>{this.state.country}</p>
                    <p>{this.state.localtime}</p>
                    <IconChange desc={this.state.description}/>
                    <p>{this.state.temperature}</p>
                    <p>{this.state.weather_descriptions}</p>
                  </div>
              </section>
             </main> 

             <footer className="footer">
                <div className="footer__left">
                    <div className="footer__left-copy">&copy; Built in 2020</div>
                </div>
                <div className="footer__right">
                    <div className="footer__right-icons">
                        <div className="footer__right-item">
                            <a className='footer__right-link' target="_blank" rel="noopener noreferrer" href="https://github.com/anyariazantceva?tab=repositories">Anna's github</a>
                        </div>
                        <div className="footer__right-item">
                            <a className="footer__right-link" target="_blank" rel="noopener noreferrer" href="https://github.com/Zionknight-crypto?tab=repositories">David's github</a>
                        </div>
                    </div>
                </div>
             </footer>
            </div>
          )
    }
}
export default App;
