import React, { Component } from 'react';
import './App.css';
import Card from './Components/Card/Card'


/*****************************************/ 
/*               API DATA                */
/*****************************************/

const API_KEY = '336eac8083532c51fc2ac7331f8f597f'
const URL = 'http://api.weatherstack.com/'

/*****************************************/ 
/*            Child Components           */
/*****************************************/

const Header = (props) => {
  return (
  <header className="header">
    <h1 className="header__h1">How's the weather today?</h1>
    <input 
    className=""
    type="search" 
    placeholder="search..." 
    onChange={this.onSearchChange()} />
  </header>
  )
}

const Main = () => {
  return (
      <div>
          <Card props={this.onSearchChange()} data={this.filteredData}/>
      </div>
  )
}

const Footer = () => {
  return (
    <footer className="footer">
      <div className="footer__left">
          <div className="footer__left-copy">&copy; Built in 2020</div>
      </div>
      <div className="footer__right">
          <div className="footer__right-icons">
              <div className="footer__right-item">
                  <a className='footer__right-link' target="_blank" rel="noopener noreferrer" href="https://github.com/anyariazantceva?tab=repositories">Anna's github</a>
              </div>
              <div className="footer__right-item">
                  <a className="footer__right-link" target="_blank" rel="noopener noreferrer" href="https://github.com/Zionknight-crypto?tab=repositories">David's github</a>
              </div>
          </div>
      </div>
    </footer>
  )
}

/*****************************************/ 
/*            App Component              */
/*****************************************/

export class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
        api: [],  
        searchfield: 'Helsinki'
    }     
  }

  componentDidMount() {
    this.getAPI()
    .then(response => response.json())
    .then(data => this.setState({

        city: data.location.name,
        country: data.location.country,
        localtime: data.location.localtime,
        weather_icons: data.current.weather_icons,
        temperature: data.current.temperature,
        weather_descriptions: data.current.weather_descriptions  

    }))
}

  getAPI() {
      return fetch(`${URL}/current?access_key=${API_KEY}&query=${this.state.searchfield}`);
  }

  onSearchChange = (event) => {
           this.setState({searchfield: event.target.value })            
  }

  render() {

    const filteredData = this.state.data.filter((data) => (
    data.name.toLowerCase().includes(this.state.searchfield.toLowerCase())))

        return (
            <div>
            <Header onChange={this.onSearchChange()} />
              <Main props={filteredData} /> 
              <Footer />
            </div>
          )
    }
}
export default App;
