import React, { Component } from 'react'
import './location.css'

// const URL = 'https://restcountries.eu/rest/v2/all'

const API_KEY = '336eac8083532c51fc2ac7331f8f597f'
const URL = 'http://api.weatherstack.com/'


export class Location extends Component {
constructor() {
    super()
    this.state={
        city: 'Helsinki',
        country: 'Finland'
        
    }
}

    componentDidMount() {

        this.getCity()
        .then(response => response.json())
        .then(cities => this.setState({currentCity: cities}))

        this.getCountry()
        .then(response => response.json())
        .then(cities => this.setState({currentCountry: cities}))

    }

    getCity() {
        return fetch(`${URL}/current?access_key=${API_KEY}&query=${this.state.city}`);
    }

    getCountry() {
        return fetch(URL);
    }

    render() {
        return (
            <div>
                <p>{ this.state.currentCity }</p> 
                <p>{ this.state.currentCountry }</p>
            </div>
        )
    }
}

export default Location;
