import React, { Component } from 'react'
import './time.css'

const URL = 'https://restcountries.eu/rest/v2/all'

export class Time extends Component {
    constructor() {
        super()
        this.state={
            time: ''
           
            
        }
    }
    


    render() {
        return (
            <div>
                <p>Time</p>
            </div>
        )
    }
}

export default Time


