import React, { Component } from 'react'
import './location.css'

// const URL = 'https://restcountries.eu/rest/v2/all'

const API_KEY = '336eac8083532c51fc2ac7331f8f597f'
const URL = 'http://api.weatherstack.com/'


export class Card extends Component {
constructor() {
    super()
    this.state={
        city: 'Helsinki',
        country: 'Finland',
        localtime: new Date().toLocaleTimeString,
        weather_icons: '',
        temperature: '',
        weather_descriptions: ''
        
    }
}

    componentDidMount() {
        this.getData()
        .then(response => response.json())
        .then(data => this.setState({
            city: data.location.name,
            country: data.location.country,
            localtime: data.location.localtime,
            weather_icons: data.current.weather_icons,
            temperature: data.current.temperature,
            weather_descriptions: data.current.weather_descriptions  
        }))

    }

    getAPI() {
        return fetch(`${URL}/current?access_key=${API_KEY}&query=${this.state.city}`);
    }

    getCountry() {
        return fetch(URL);
    }

    render() {
        return (
            <div>
                <p>{ this.state.city }</p> 
                <p>{ this.state.country }</p>
                <p>{ this.state.localtime }</p>
                <img alt="icon" src={ this.state.weather_icons } />
                <p>{ this.state.temperature }</p>
                <p>{ this.state.weather_descriptions }</p>
            </div>
        )
    }
}

export default Card;

