import React, { Component } from 'react';
import './App.css';
import Header from './Components/Header/Header'
import Main from './Components/Main/Main'
import Footer from './Components/Footer/Footer'



const API_KEY = '336eac8083532c51fc2ac7331f8f597f'
const URL = 'http://api.weatherstack.com/'

class App extends Component {
  constructor() {
    super()
    this.state = {
        api: [],  // change robots to [] if you want to use API
        searchfield: ''
    }     
  }

  componentDidMount() {
      this.getData()
      .then(response => response.json())
      .then(data => this.setState({
          api: data
      }))

  }

  getAPI() {
      return fetch(`${URL}/current?access_key=${API_KEY}&query=${this.state.city}`);
  }

    render() {
      onSearchChange = (event) => {
        this.setState({searchfield: event.target.value })   
    }
    const filteredData = this.state.data.filter(data => {
      return data.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
      })
      if(this.state.data.length === 0) {
          return <h1>Loading...</h1>
      } else { 
        return (
        <div>
          <Header />
          <Main />
          <Footer />
        </div>
      )
    }
  }
}

export default App;
