import './location.css'

const URL = 'https://restcountries.eu/rest/v2/all'

export class Location extends Component {
constructor() {
    super()
    this.state={
        city: '',
        currentCity: [],
        country: '',
        currentCountry: []
    }
}

componentDidMount() {

    this.getFlag()
    .then(response => response.json())
    .then(cities => this.setState({currentCity: cities}));
};

getFlag() {

return fetch(URL);

};


    render() {
